import "./App.css";
import { useState } from "react";

function App() {
  const [time, setTime] = useState(1);

  const handleClick = () => {
    // setTime(time + 1);
    let newTime;
    if (time >= 12) {
      newTime = 1;
    } else {
      newTime = time + 1;
    }
    setTime(newTime);
  };
  return (
    <div>
      <span>현재 시각: {time}시</span>
      <button onClick={handleClick}>Update</button>
    </div>
  );
}

export default App;
